coloumn1.on('click',function () {
  var y = coloumn1Color.length;
  switch (y) {
    case 0:
    coloumn1.eq(4).css("background",bgColor);
    coloumn1Color.unshift(bgColor);

    if(bgColor == "red")
      bgColor = "blue";
    else
      bgColor = "red";
      console.log(bgColor);

      break;
      case 1:
       coloumn1.eq(3).css("background",bgColor);
        coloumn1Color.unshift(bgColor);
        if(bgColor == "red")
          bgColor = "blue";
        else
          bgColor = "red";
            console.log(bgColor);

      break;
      case 2:
       coloumn1.eq(2).css("background",bgColor);
        coloumn1Color.unshift(bgColor);
        if(bgColor == "red")
          bgColor = "blue";
        else
          bgColor = "red";
            console.log(bgColor);
      break;
      case 3:
       coloumn1.eq(1).css("background",bgColor);
        coloumn1Color.unshift(bgColor);
        if(bgColor == "red")
          bgColor = "blue";
        else
          bgColor = "red";
            console.log(bgColor);
      break;
      case 4:
       coloumn1.eq(0).css("background",bgColor);
        coloumn1Color.unshift(bgColor);
        if(bgColor == "red")
          bgColor = "blue";
        else
          bgColor = "red";
            console.log(bgColor);
      break;

    default:

  }

})

